import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, IconButton } from "@material-ui/core";
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        textAlign: 'center'
    },
    gridItemLinkedIn: {
        fontSize: '25px',
        textAlign: 'left'
    },
    linkedInLogo: {
        fontSize: '100px'
    },
    gridItemInfo: {
        textAlign: 'left'
    },
    email: {
        fontSize: '25px',
        marginTop: '25px',
        marginBottom: '0px'
    },
    phone: {
        fontSize: '25px',
        marginTop: '0px'
    }
}));

export default function CenteredGrid() {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                <Grid item xs={12} sm={2} className={classes.gridItemLinkedIn}>
                    <IconButton
                        title="LinkedIn"
                        aria-label="LinkedIn"
                        href={'https://www.linkedin.com/in/kevin-schroeder-5b9b738a/'}
                    >
                        <LinkedInIcon className={classes.linkedInLogo} fontSize="large"/>
                    </IconButton>
                </Grid>
                <Grid item xs={12} sm={10} className={classes.gridItemInfo}>
                    <p className={classes.email}>schroederkevin94@gmail.com</p>
                    <p className={classes.phone}>(847) 271-3732</p>
                </Grid>
            </Grid>
        </div>
    );
}
